import React , {useState, useEffect} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import {Form,Container,Row,Col,Alert,Card} from 'react-bootstrap';

function About() {
  const [show, setShow] = useState(false);
  const [coment, setComent] = useState("");

  useEffect(() => {
    if (coment !== "") {
      setShow(true);
    } else {
      setShow(false);
    }
  }, [coment]);

  return (
    <div>
      <Container className="Container">
        <Row className="justify-content-center">
          <Col xs lg="3">
            <Form>
              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>
                  {" "}
                  <h2> Share your opinion in Photo Flowers</h2>
                </Form.Label>
                <Form.Control
                  className="list-input"
                  type="text"
                  placeholder="Enter Your Coment..."
                  onChange={(e) => {
                    setComent(e.target.value);
                  }}
                />
              </Form.Group>
            </Form>
          </Col>
        </Row>
        {show ? (
          <Alert variant="primary" className="alert">
            <Alert.Heading>{coment}</Alert.Heading>
            <p className="mb-0"></p>
          </Alert>
        ) : (
          ""
        )}
      </Container>

      <Card>
        <Card.Header>
          <h2>About Photo Flowers</h2>
        </Card.Header>
        <Card.Body>
          <blockquote className="blockquote mb-0">
            <p>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer
              posuere erat a ante.
            </p>
            <footer className="blockquote-footer">
              Someone famous in <cite title="Source Title">Source Title</cite>
            </footer>
          </blockquote>
        </Card.Body>
      </Card>
    </div>
  );
}

export default About;
